/*  This sql creates a template based upon the options group.  In that sense it is somewhat backwards, however the
    results are be the same as if the template were created first. */
	
DECLARE @POSOptionGroupId INT
DECLARE @POSOptionTemplateId INT

DECLARE @ClientId INT
SELECT @ClientId = MAX(client_id) FROM RAD_SYS_Client

DECLARE @NumberOfTickets INT
DECLARE @NextTicket INT
SET @NumberOfTickets = 1

-- There should only be one group
SELECT @PosOptionGroupId = MAX(pos_option_group_id) FROM POS_Option_Group

-- Get Rid of any template link
UPDATE POS_Option_Group 
SET pos_option_template_id = NULL
WHERE pos_option_group_id = @POSOptionGroupId

-- Get Rid of the templates
DELETE POS_Option_Template_List WHERE client_id = @ClientId
DELETE POS_Option_Template WHERE client_id =  @ClientId

-- Create a new template
EXEC plt_get_next_named_ticket 'POS_Option_Template','n', @NumberOfTickets, @NextTicket OUTPUT
SET @POSOptionTemplateId = @NextTicket

INSERT POS_Option_Template (pos_option_template_id, name, description, client_id, last_modified_user_id, last_modified_timestamp, pos_type_code)
VALUES (@POSOptionTemplateId, N'Default POS Option Template', N'Default PCS Options', @ClientId, 42, GETDATE(), 'l')

-- Set the option Group to use the template
UPDATE POS_Option_Group 
SET pos_option_template_id = @POSOptionTemplateId
WHERE pos_option_group_id = @POSOptionGroupId

-- Create the template options based upon the group
INSERT POS_Option_Template_List (pos_option_template_id, pos_option_id, Client_Id, value_editable_flag, last_modified_user_id, last_modified_timestamp)
SELECT @POSOptionTemplateId, pos_option_id, @ClientId, value_editable_flag, 42, GETDATE()
FROM POS_Option_Group_List
WHERE pos_option_group_id = @POSOptionGroupId

